CREATE DEFINER=`stc_admin`@`%` PROCEDURE `verifyUser` (IN `uEmail` VARCHAR(2047), IN `uPassword` VARCHAR(2047))   begin
set @count = (Select count(1) from users where userEmail = uEmail);
if(@count = 0)
then
select "Not Found" as result;
end if ;
if(@count <> 0)
then
set @fetchPass = (Select userPassword from users where userEmail = uEmail);
if(@fetchPass = uPassword)
then
set @uType = (Select userType from users where userEmail = uEmail);
select @uType,"Valid" as result;
end if;
if(@fetchPass <> uPassword)
then
select "InValid" as result;
end if;
end if;
end$$

